		hlm('flex w-full h-2 px-3 self-center relative transition-all', this.userClass()),
	);

	protected readonly _brnSlider = inject(BRN_SLIDER);
	protected readonly _brnSliderTrack = inject(BRN_SLIDER_TRACK, { host: true });
}

